    navOutput = "<ul>";
    $nav.addClass("features-nav");
    for (var i = 0, len = rsInstance.numSlides; i < len; i++) {
        navOutput += '<li><a href="#"><span>' + (i + 1) + "</span></a></li>";
    }
    navOutput += "</ul>";
